INSERT INTO Movie (image, awards, title, rating, releaseDate, length, ageRating, genre, description) VALUES
                                                                                                         ('inception', 'Oscar Winner', 'Inception', 9, '2010-07-16', 148, 12, 'Science Fiction', 'Dom Cobb ist ein begabter Dieb und in der gefährlichen Kunst der Extraction spezialisiert: Er stiehlt wertvolle Geheimnisse aus dem Unterbewusstsein während des Traums. Diese seltene Fähigkeit macht ihn zu einem begehrten Spieler in der Welt der Wirtschaftsspionage, kostet ihn jedoch alles, was er liebt. Cobb erhält die Chance auf Erlösung: Ein letzter Job könnte ihm sein Leben zurückgeben, aber nur wenn er das Unmögliche schafft - Inception. Statt einen Traum zu stehlen, muss er eine Idee einpflanzen.'),
